import React, { Component } from 'react';
import Comments from './Comments'

class CommentsDisplay extends Component {

  render() {
    return (
      <div className="CommentsDisplay">

        <h2 id="comments-area__h2">Join the Conversation</h2>

        {/* <div class="comments-container">
            <p class="comments-container__title">NAME</p>

            <div class="comments-area__name-input">

            </div>
        </div> */}

            <input type="text" name="comment" placeholder="Write a comment" id="commentInput" />

        <img src="./4.0 - Assets/Images/Mohan-muruge.jpg" class="profile-picture" alt="" />

        <p class="comments-container__title">COMMENT</p>

        <div class="comments-area__comment-input">

        <button>COMMENT</button>
        </div>

      </div>
    )
  }
}

export default CommentsDisplay;
