import React, { Component } from "react";
import { Switch, Route } from "react-router-dom";
import Nav from "./Nav";
import Video from "./Video";
import VideoInfo from "./VideoInfo";
import NextVideoList from "./NextVideoList";
import NextVideo from "./NextVideo";
import CommentsDisplay from "./CommentsDisplay";
import logo from "../BrainFlix-Sprint-1/4.0 - Assets/Logo/Logo-brainflix.png";
import profilePicture from "../BrainFlix-Sprint-1/4.0 - Assets/Images/Mohan-muruge.jpg";
import "../styles/App.css";
import "../styles/global.css";
import axios from "axios";

const api_key = "4edb152d-f186-4793-836c-d5befa07ea2f";
const videosUrl = `https://project-2-api.herokuapp.com/videos/?api_key=${api_key}`;

const mainVideoUrl = id =>
  `https://project-2-api.herokuapp.com/videos/${id}?api_key=${api_key}`;


class Home extends Component {
  state = {
    videos: [],
    mainVideo: null,
    commentList: []
  };

  //VIDEO API
  componentDidMount() {
    axios
      .get(videosUrl)
      .then(res => {
        this.setState({
          videos: res.data
        });
      })
      .then(res => {
        const videoId = this.props.match.params.id
        axios.get(mainVideoUrl(videoId)).then(res => {
          this.setState({
            mainVideo: res.data,
            commentList: res.data.comments
          });
        });
      });
  }




  render() {
    if (this.state.mainVideo === null) return "Loading...";
    console.log(this.state.videos)
    return (
      <div>
        {/* <Switch>
          <Route path="/video/:id" exact render={(props) => {
            const videoData = this.state.videos.find(video => {
              return (props.match.params.id === video.id)
            })
            return <NextVideo mainVideoUrl={this.mainVideoUrl} video={videoData} />
          }} />
        </Switch> */}


        <Nav logo={logo} profilePicture={profilePicture} />
        <Video image={this.state.mainVideo.image}/>
        <div className="more-content">
          <div className="more-content__this-video">
            <VideoInfo mainVideo={this.state.mainVideo} />
            <CommentsDisplay
              profilePicture={profilePicture}
              userComments={this.state.commentList}
            />
          </div>
          <aside className="more-content__next-videos">
            <NextVideoList sideVideo={this.state.videos} switchVideo={this.switchVideo} />
          </aside>
        </div>
      </div>
    );
  }
}

export default Home;
