import React, { Component } from "react";
import likesIcon from "../BrainFlix-Sprint-1/4.0 - Assets/Icons/PNG/Icon-likes.png";
import viewsIcon from "../BrainFlix-Sprint-1/4.0 - Assets/Icons/PNG/Icon-views.png";

class VideoInfo extends Component {
  render() {

    const currentVideo = this.props.mainVideo.map(video => {
      return (
        <VideoInfo
          title={video.title}
          description={video.description}
          channel={video.channel}
          image={video.image}
          views={video.views}
          likes={video.likes}
          timestamp={video.timestamp}
        />
      );
    });

    return (
      <section className="VideoInfo">
        <h1 className="VideoInfo__header">{this.props.title}</h1>

        <div className="VideoInfo__details">
          <span className="user">By {this.props.channel}</span>
          <span className="date">{this.props.timestamp}</span>

          <span className="likes">{this.props.likes}</span>
          <img className="likes__icon" src={likesIcon} alt="heart-icon" />

          <span className="views">{this.props.views}</span>
          <img className="views__icon" src={viewsIcon} alt="view-icon" />
        </div>
        <div className="divider" />

        <p className="VideoInfo__p">{this.props.description}</p>
      </section>
    );
  }
}

export default VideoInfo;
